type RolePermission = {
    resource: string;
    action: number;
} & Partial<{
    schedAction: number;
    schedCron: string;
    schedRange: string[];
}>;
interface Role {
    permissions: RolePermission[];
    roleId: string;
    roleName: string;
    isWildcard: boolean;
    adminRole: boolean;
}
interface RoleUser {
    displayName: string;
    namespace: string;
    userId: string;
}
type RoleWithManager = Role & {
    managers: RoleUser[];
};
interface NamespaceRole {
    roleId: string;
    namespace: string;
}
interface NamespaceRoleWithPermission extends NamespaceRole {
    permissions: RolePermission[];
}

export type { NamespaceRole, NamespaceRoleWithPermission, Role, RolePermission, RoleUser, RoleWithManager };
