import { z } from 'zod';

declare const ValidateBadWordsErrorType: z.ZodEnum<["empty", "containsBadWords"]>;
type ValidateBadWordsErrorType = z.infer<typeof ValidateBadWordsErrorType>;
/**
 * Tokenize and then validate bad word. The tokenizer will separate the value by
 * treating non alphanumeric character as separator.
 *
 * @param value the input value to be validate
 */
declare function tokenizeAndValidateBadWords(value: string): "containsBadWords" | null;

export { ValidateBadWordsErrorType, tokenizeAndValidateBadWords };
